import React, { useState } from 'react'

const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas' }
  ]) 
  const [ newName, setNewName ] = useState('')
  const setToNewName = () => setNewName(newName)

  const  Person = ({person}) => {
    return (
        <div key={person.id}>
            {person.id} {person.name} {person.number}
        </div>
    )
}
  const [showAll, setShowAll] = useState(true)  
  const personsToShow = showAll  ? persons : persons.filter(person => person.important === true)

  const handleNameChange = (event) => {
    //setToNewName(event.target.value);
    e.preventDefault();
    this.props.addPerson(this.state)
};


  return (
    <div style={{margin:'4px'}} >
      <h2>Phonebook</h2>
      <form onSubmit={setPersons}>
        <div>
          name: <input id="name" type="text" onChange={handleNameChange} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <div>
        {personsToShow.map(person => <Person key={person.id} person={person} />)}
      </div>
    </div>
  )
}

export default App;
