import React, { useState } from 'react'

const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas' }
  ]) 
  const [ newName, setNewName ] = useState('')
  const setToNewName = () => setNewName(newName)

  const handleNameChange = (event) => {
    setToNewName(event.target.value);
};

  return (
    <div style={{margin:'4px'}} >
      <h2>Phonebook</h2>
      <form onSubmit={setPersons}>
        <div>
          name: <input id="name" type="text" onChange={handleNameChange} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <div>
      {persons.map(person => <div>{person.name}</div>)}
      </div>
    </div>
  )
}

export default App;
