import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Button =(props) =>(
  <button onClick={props.handleClick}>{props.text}</button>
)

const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas' }
  ]) 
  const [ newName, setNewName ] = useState('')
  const setToNewName = () => setNewName(newName)
  //const setToPersons = () => setPersons(persons)

  const handleNameChange = (event) => {
    setToNewName(event.target.value);
    console.log(event.target.value);
  };

  const setToPersons = (event) => {
    event.preventDefault();
    React.setState(prevState => {
      let newPersons = [prevState.persons];
      newPersons[prevState.index].persons.push(newName);
      //return {persons: newPersons};
      persons = newPersons;
    });
  };

/*doSomething = function (event) {
  event.preventDefault();
  this.setState(prevState => {
    let newPersons = [prevState.persons];
    newPersons[prevState.index].persons.push(newName);
    return {persons: newPersons};
  });
  onSubmit={(e) => {this.doSomething(); e.preventDefault();}}
}*/

  return (
    <div style={{margin:'4px'}}>
      <h2>Phonebook</h2>
      <form>
        <div>
          name: <input id="name" type="text" onChange={handleNameChange} />
        </div>
        <div>
        <Button handleClick={setToPersons} text="add" />
        </div>
      </form>
      <h2>Numbers</h2>
      <div>
      {persons.map(person => <div>{person.name}</div>)}
      </div>
    </div>
  )
}

export default App;
